import { Button, Rating, TextField, Typography } from '@mui/material'
import { blue } from '@mui/material/colors';
import React, { useState } from 'react'

const Form = () => {
    const [formData, setFormData] = useState({
        name: '',
        phoneNumber: '',
        rating: 0,
        comments: ''
    });

    const handleChange = (field, value) => {
        setFormData({
            ...formData,
            [field]: value
        });
    };


    const handleSubmit = () => {
        // Convert formData to a JSON string
        const jsonData = JSON.stringify(formData);

        // Create a Blob containing the JSON data
        const blob = new Blob([jsonData], { type: 'application/json' });

        // Create a download link for the Blob
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'formData.json';
        document.body.appendChild(a);

        // Trigger a click event on the download link to start the download
        a.click();

        // Remove the download link from the document
        document.body.removeChild(a);
    };

    return (
        <div className='form'>
            <Typography  style={{ fontWeight: 300 }} component="legend" className='heading' fontSize={40} >Sustainable Impact Feedback Form</Typography>
            <TextField
                id="name"
                label="Name"
                variant="outlined"
                onChange={(e) => handleChange('name', e.target.value)}
            />
            <TextField
                id="phoneNumber"
                label="Phone Number"
                variant="outlined"
                onChange={(e) => handleChange('phoneNumber', e.target.value)}
            />

            <Typography component="legend">How do you rate our service:</Typography>
            <Rating

                name="simple-controlled"
                value={formData.rating}
                onChange={(_, newValue) => handleChange('rating', newValue)}
                style={{ color: '#0099FF' }} // Set the color to blue
            />

            <TextField
                id="comments"
                label="Comments"
                variant="outlined"
                multiline
                rows={4}
                onChange={(e) => handleChange('comments', e.target.value)}
            />

            <Button variant="contained" onClick={(e) => handleSubmit(e)}>SUBMIT</Button>
        </div>
    );
}

export default Form;
